#!/usr/bin/env python3
"""
Professional AI Image Detection Web Application
Flask-based web app with advanced AI detection capabilities
Maintains all original design elements while using Python backend
"""

import os
import sys
import json
import time
import uuid
from io import BytesIO
import base64
from flask import Flask, render_template, request, jsonify, send_from_directory
from werkzeug.utils import secure_filename
from PIL import Image
import numpy as np
import cv2
try:
    from enhanced_ai_detector import EnhancedAIImageDetector
    detector = EnhancedAIImageDetector()
    print("Using enhanced AI detector with deep learning support", file=sys.stderr)
except Exception as e:
    print(f"Enhanced detector not available ({e}), using simple detector", file=sys.stderr)
    from simple_ai_detector import SimpleAIImageDetector
    detector = SimpleAIImageDetector()

app = Flask(__name__)
app.config['MAX_CONTENT_LENGTH'] = 16 * 1024 * 1024  # 16MB max file size
app.config['UPLOAD_FOLDER'] = 'temp_uploads'

# Ensure upload directory exists
os.makedirs(app.config['UPLOAD_FOLDER'], exist_ok=True)

# AI detector is initialized above during import

# Allowed file extensions
ALLOWED_EXTENSIONS = {'png', 'jpg', 'jpeg', 'gif', 'bmp', 'webp'}

def allowed_file(filename):
    """Check if file extension is allowed"""
    return '.' in filename and \
           filename.rsplit('.', 1)[1].lower() in ALLOWED_EXTENSIONS

def process_image_buffer(image_buffer):
    """Process image buffer to get metadata and prepare for analysis"""
    try:
        # Open image with PIL to get metadata
        pil_image = Image.open(BytesIO(image_buffer))
        width, height = pil_image.size

        # Convert to RGB if necessary
        if pil_image.mode != 'RGB':
            pil_image = pil_image.convert('RGB')

        # Get file size
        file_size = len(image_buffer)

        return {
            'width': width,
            'height': height,
            'size': file_size,
            'format': pil_image.format or 'Unknown'
        }
    except Exception as e:
        print(f"Error processing image: {e}")
        return None

@app.route('/')
def index():
    """Main page route"""
    return render_template('index.html')

@app.route('/how-it-works')
def how_it_works():
    """How it works page route"""
    return render_template('how-it-works.html')

@app.route('/about')
def about():
    """About page route"""
    return render_template('about.html')

@app.route('/faq')
def faq():
    """FAQ page route"""
    return render_template('faq.html')

@app.route('/api/analyze', methods=['POST'])
def analyze_image():
    """API endpoint for image analysis"""
    if 'image' not in request.files:
        return jsonify({'error': 'No image file provided'}), 400

    file = request.files['image']
    if file.filename == '':
        return jsonify({'error': 'No image file selected'}), 400

    if not allowed_file(file.filename):
        return jsonify({'error': 'Invalid file type. Please upload an image file.'}), 400

    try:
        # Read image data
        image_buffer = file.read()

        # Process image metadata
        metadata = process_image_buffer(image_buffer)
        if not metadata:
            return jsonify({'error': 'Invalid image file'}), 400

        # Start analysis timer
        start_time = time.time()

        # Create temporary file for analysis (required by detector)
        temp_filename = str(uuid.uuid4()) + '.jpg'
        temp_path = os.path.join(app.config['UPLOAD_FOLDER'], temp_filename)

        # Save temporary file
        with open(temp_path, 'wb') as temp_file:
            temp_file.write(image_buffer)

        # Perform AI detection analysis
        try:
            with open(temp_path, 'rb') as temp_file:
                temp_buffer = temp_file.read()
            analysis_result = detector.detect_ai_image(temp_buffer)
        finally:
            # Clean up temporary file
            if os.path.exists(temp_path):
                os.remove(temp_path)

        # Calculate processing time
        processing_time = round(time.time() - start_time, 2)

        # Format response
        response_data = {
            'id': str(uuid.uuid4()),
            'classification': analysis_result['classification'],
            'confidence': analysis_result['confidence'],
            'processingTime': processing_time,
            'imageSize': f"{metadata['width']}x{metadata['height']}",
            'indicators': [
                {'name': indicator, 'strength': 'Strong' if i < 2 else 'Moderate' if i < 4 else 'Weak'} 
                for i, indicator in enumerate(analysis_result['indicators'][:6])
            ],
            'filename': secure_filename(file.filename or 'unknown.jpg'),
            'originalSize': metadata['size']
        }

        return jsonify(response_data)

    except Exception as e:
        print(f"Analysis error: {e}")
        return jsonify({'error': 'Failed to analyze image. Please try again.'}), 500

@app.route('/static/<path:filename>')
def static_files(filename):
    """Serve static files"""
    return send_from_directory('static', filename)

@app.errorhandler(413)
def too_large(e):
    """Handle file too large error"""
    return jsonify({'error': 'File too large. Maximum file size is 16MB.'}), 413

@app.errorhandler(404)
def not_found(e):
    """Handle 404 errors"""
    return render_template('404.html'), 404

if __name__ == '__main__':
    # Run the Flask application
    app.run(host='0.0.0.0', port=5000, debug=True)

