/* Professional Tailwind CSS Enhancements */

/* Smooth scrolling for all browsers */
html {
    scroll-behavior: smooth;
}

/* Custom scrollbar styling */
::-webkit-scrollbar {
    width: 6px;
}

::-webkit-scrollbar-track {
    background: transparent;
}

::-webkit-scrollbar-thumb {
    background: rgba(156, 163, 175, 0.5);
    border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
    background: rgba(156, 163, 175, 0.7);
}

.dark ::-webkit-scrollbar-thumb {
    background: rgba(75, 85, 99, 0.5);
}

.dark ::-webkit-scrollbar-thumb:hover {
    background: rgba(75, 85, 99, 0.7);
}

/* Firefox scrollbar */
* {
    scrollbar-width: thin;
    scrollbar-color: rgba(156, 163, 175, 0.5) transparent;
}

.dark * {
    scrollbar-color: rgba(75, 85, 99, 0.5) transparent;
}

/* Professional focus states */
button:focus-visible,
input:focus-visible,
select:focus-visible,
textarea:focus-visible,
a:focus-visible {
    outline: 2px solid #3b82f6;
    outline-offset: 2px;
}

/* Enhanced Glassmorphism - Responsive */
.glass {
    background: rgba(255, 255, 255, 0.25);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.18);
    border-radius: 1.5rem;
    transition: all 0.3s ease;
}

.dark .glass {
    background: rgba(31, 41, 55, 0.25);
    border: 1px solid rgba(75, 85, 99, 0.18);
}

/* Responsive glass effects */
@media (max-width: 480px) {
    .glass {
        backdrop-filter: blur(8px);
        border-radius: 1rem;
        background: rgba(255, 255, 255, 0.3);
    }

    .dark .glass {
        background: rgba(31, 41, 55, 0.3);
    }
}

@media (min-width: 1281px) {
    .glass {
        backdrop-filter: blur(15px);
        border-radius: 2rem;
    }
}

/* Performance optimization for low-end devices */
@media (max-width: 768px) and (max-resolution: 192dpi) {
    .glass {
        backdrop-filter: blur(5px);
    }
}

/* Enhanced Hover Effects - Responsive */
.hover-lift {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Only apply hover effects on devices that support hover */
@media (hover: hover) and (pointer: fine) {
    .hover-lift:hover {
        transform: translateY(-2px);
    }
}

/* Touch feedback for mobile devices */
@media (hover: none) and (pointer: coarse) {
    .hover-lift:active {
        transform: scale(0.98);
        transition: transform 0.1s ease;
    }
}

/* Reduced motion accessibility */
@media (prefers-reduced-motion: reduce) {
    .hover-lift,
    .hover-lift:hover,
    .hover-lift:active {
        transform: none !important;
        transition: none !important;
    }
}

/* Professional gradient text */
.gradient-text {
    background: linear-gradient(135deg, #3b82f6, #8b5cf6, #7ADAA5);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* Enhanced button interactions */
.btn-hover-scale {
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.btn-hover-scale:hover {
    transform: scale(1.05);
}

.btn-hover-scale:active {
    transform: scale(0.98);
}

/* Enhanced Loading States - Responsive */
.loading-ring {
    position: relative;
}

.loading-ring::after {
    content: '';
    position: absolute;
    inset: 0;
    border: 2px solid transparent;
    border-top: 2px solid #3b82f6;
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

/* Responsive loading sizes */
@media (max-width: 480px) {
    .loading-ring {
        width: 1.5rem;
        height: 1.5rem;
    }

    .w-20 {
        width: 3rem;
    }

    .h-20 {
        height: 3rem;
    }
}

@media (min-width: 481px) and (max-width: 768px) {
    .loading-ring {
        width: 2rem;
        height: 2rem;
    }
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Enhanced Image Container - Responsive */
.image-container {
    position: relative;
    overflow: hidden;
    border-radius: 1rem;
}

.image-container img {
    transition: transform 0.3s ease;
    width: 100%;
    height: 100%;
    object-fit: cover;
}

/* Only apply hover effects on non-touch devices */
@media (hover: hover) and (pointer: fine) {
    .image-container:hover img {
        transform: scale(1.02);
    }
}

/* Responsive image sizing */
@media (max-width: 480px) {
    .image-container {
        border-radius: 0.5rem;
    }

    .aspect-square {
        aspect-ratio: 1 / 1;
        max-width: 200px;
        margin: 0 auto;
    }
}

@media (min-width: 481px) and (max-width: 768px) {
    .aspect-square {
        max-width: 280px;
        margin: 0 auto;
    }
}

@media (min-width: 769px) {
    .aspect-square {
        max-width: 350px;
    }
}

/* Enhanced Accessibility and Device Support */

/* Touch device specific styles */
.touch-device button,
.touch-device .btn-hover-scale {
    min-height: 44px;
    min-width: 44px;
    touch-action: manipulation;
}

.touch-device .hover-lift:hover {
    transform: none;
}

.touch-device .hover-lift:active {
    transform: scale(0.98);
}

/* Keyboard navigation support */
.keyboard-navigation *:focus {
    outline: 2px solid #3b82f6 !important;
    outline-offset: 2px !important;
    border-radius: 4px;
}

.keyboard-navigation button:focus,
.keyboard-navigation a:focus {
}

/* Viewport height handling for mobile browsers */
:root {
    --vh: 1vh;
}

.min-h-screen {
    min-height: calc(var(--vh, 1vh) * 100);
}

/* Enhanced mobile menu animations */
#mobile-menu {
    transform-origin: top center;
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

#mobile-menu.hidden {
    opacity: 0;
    transform: translateY(-10px);
    pointer-events: none;
}

#mobile-menu:not(.hidden) {
    opacity: 1;
    transform: translateY(0);
    pointer-events: all;
}

/* Theme toggle enhancements */
#theme-toggle {
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

#theme-toggle:active {
    transform: scale(0.95);
}

/* Mobile menu toggle improvements */
#mobile-menu-toggle {
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

#mobile-menu-toggle i {
    transition: transform 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }

    html {
        scroll-behavior: auto;
    }

    .hover-lift,
    .hover-lift:hover,
    .hover-lift:active {
        transform: none !important;
    }

    #mobile-menu,
    #theme-toggle,
    #mobile-menu-toggle {
        transition: none !important;
    }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    .glass {
        background: rgba(255, 255, 255, 0.9);
        border: 2px solid rgba(0, 0, 0, 0.2);
    }

    .dark .glass {
        background: rgba(31, 41, 55, 0.9);
        border: 2px solid rgba(255, 255, 255, 0.2);
    }
}

/* Print styles */
@media print {
    .no-print {
        display: none !important;
    }

    body {
        background: white !important;
        color: black !important;
    }

    .glass {
        background: white !important;
        border: 1px solid #ccc !important;
    }

    nav, footer {
        display: none !important;
    }
}

/* Professional toast notification positioning */
#toast-container {
    position: fixed;
    top: 6rem;
    right: 1.5rem;
    z-index: 100;
    max-width: 24rem;
}

/* Comprehensive Responsive Design System */

/* Mobile First - 320px to 480px */
@media (max-width: 480px) {
