}

/* Real Image - Success Styling */
.badge-real {
    background: linear-gradient(135deg, #7ADAA5 0%, #7ADAA5 100%);
    color: white;
    border: 2px solid #7ADAA5;
}

.badge-real:hover {
    transform: scale(1.02);
}

/* Unknown/Error - Neutral Styling */
.badge-unknown {
    background: linear-gradient(135deg, #6b7280 0%, #4b5563 100%);
    color: white;
    border: 2px solid #d1d5db;
}

/* Progress Bar System */
.progress-bar {
    height: 12px;
    border-radius: 6px;
    transition: all 1s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
}

.progress-bar::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        90deg,
        transparent,
        rgba(255, 255, 255, 0.2),
        transparent
    );
    animation: shimmer 2s infinite;
}

@keyframes shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Force AI Generated Progress Bar - Professional Red */
div[id="confidence-bar"].progress-bar-ai-generated,
#confidence-bar.progress-bar-ai-generated,
.progress-bar-ai-generated {
    background: #dc2626 !important;
    background-image: linear-gradient(90deg, #dc2626 0%, #b91c1c 50%, #991b1b 100%) !important;
    background-color: #dc2626 !important;
    border: 2px solid rgba(220, 38, 38, 0.5) !important;
}

/* Force Real Image Progress Bar - Professional Green */
div[id="confidence-bar"].progress-bar-real,
#confidence-bar.progress-bar-real,
.progress-bar-real {
    background: #7ADAA5 !important;
    background-image: linear-gradient(90deg, #7ADAA5 0%, #5fb88b 50%, #4a9a72 100%) !important;
    background-color: #7ADAA5 !important;
    border: 2px solid rgba(122, 218, 165, 0.5) !important;
}

/* Override any dark mode or other conflicting styles */
.dark #confidence-bar.progress-bar-ai-generated,
.dark div[id="confidence-bar"].progress-bar-ai-generated {
    background: #dc2626 !important;
    background-image: linear-gradient(90deg, #dc2626 0%, #b91c1c 50%, #991b1b 100%) !important;
    background-color: #dc2626 !important;
}

.dark #confidence-bar.progress-bar-real,
.dark div[id="confidence-bar"].progress-bar-real {
    background: #7ADAA5 !important;
    background-image: linear-gradient(90deg, #7ADAA5 0%, #5fb88b 50%, #4a9a72 100%) !important;
    background-color: #7ADAA5 !important;
}

/* Ensure no other styles can override */
#confidence-bar[style*="background"] {
    /* This selector has higher specificity for inline styles */
}

/* Additional fallback with element selector */
div#confidence-bar {
    transition: all 1000ms ease-out !important;
    border-radius: 9999px !important;
    position: relative !important;
}

/* Enhanced Result Cards - Responsive */
.result-card {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 1.5rem;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    padding: 1.5rem;
    margin-bottom: 1rem;
}

.dark .result-card {
    background: rgba(31, 41, 55, 0.95);
    border: 1px solid rgba(75, 85, 99, 0.2);
}

/* Hover effects only on non-touch devices */
@media (hover: hover) and (pointer: fine) {
    .result-card:hover {
        transform: translateY(-2px);
    }
}

/* Responsive card sizing */
@media (max-width: 480px) {
    .result-card {
        border-radius: 1rem;
        padding: 1rem;
        margin-bottom: 0.75rem;
    }
}

@media (min-width: 481px) and (max-width: 768px) {
    .result-card {
        padding: 1.25rem;
    }
}

@media (min-width: 1281px) {
    .result-card {
        border-radius: 2rem;
        padding: 2rem;
        margin-bottom: 1.5rem;
    }
}

/* Professional Circular Image Border System */
.image-ai-generated {
    border: 4px solid #ef4444;
    border-radius: 50%;
    position: relative;
    overflow: hidden;
}

.image-real {
    border: 4px solid #7ADAA5;
    border-radius: 50%;
    position: relative;
    overflow: hidden;
}

/* Motion Wave Effects */
.image-ai-generated::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: conic-gradient(transparent, rgba(239, 68, 68, 0.3), transparent, rgba(239, 68, 68, 0.3));
    animation: rotate-wave 3s linear infinite;
    border-radius: 50%;
    z-index: -1;
}

.image-real::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: conic-gradient(transparent, rgba(122, 218, 165, 0.3), transparent, rgba(122, 218, 165, 0.3));
    animation: rotate-wave 3s linear infinite;
    border-radius: 50%;
    z-index: -1;
}

@keyframes rotate-wave {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Enhanced Indicator Badges - Responsive */
.indicator-strong {
    background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
    color: white;
    font-size: 0.75rem;
    padding: 0.25rem 0.75rem;
    border-radius: 0.75rem;
    font-weight: 600;
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
}

.indicator-moderate {
    background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
    color: white;
    font-size: 0.75rem;
    padding: 0.25rem 0.75rem;
    border-radius: 0.75rem;
    font-weight: 600;
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
}

.indicator-weak {
    background: linear-gradient(135deg, #3b82f6 0%, #2563eb 100%);
    color: white;
    font-size: 0.75rem;
    padding: 0.25rem 0.75rem;
    border-radius: 0.75rem;
    font-weight: 600;
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
}

/* Responsive indicator sizing */
@media (max-width: 480px) {
    .indicator-strong,
    .indicator-moderate,
    .indicator-weak {
        font-size: 0.625rem;
        padding: 0.2rem 0.5rem;
        border-radius: 0.5rem;
        gap: 0.125rem;
    }
}

@media (min-width: 769px) {
    .indicator-strong,
    .indicator-moderate,
    .indicator-weak {
        font-size: 0.875rem;
        padding: 0.375rem 1rem;
        border-radius: 1rem;
        gap: 0.375rem;
    }
}

/* Enhanced Wave Animation for AI Detection */
.ai-detected-wave {
    animation: warning-pulse 1.5s ease-in-out infinite, warning-shake 3s ease-in-out infinite;
    position: relative;
}

.real-detected-wave {
    animation: success-pulse 3s ease-in-out infinite, gentle-float 4s ease-in-out infinite;
    position: relative;
}

/* Warning waves - Multiple aggressive ripples for AI Generated */
.ai-detected-wave::before {
    content: '';
    position: absolute;
    top: -15px;
    left: -15px;
    right: -15px;
    bottom: -15px;
    border-radius: 50%;
    border: 3px solid rgba(239, 68, 68, 0.4);
    animation: warning-ripple-1 1.5s infinite;
    z-index: -1;
}

.ai-detected-wave::after {
    content: '';
    position: absolute;
    top: -25px;
    left: -25px;
    right: -25px;
    bottom: -25px;
    border-radius: 50%;
    border: 2px solid rgba(239, 68, 68, 0.2);
    animation: warning-ripple-2 1.5s infinite 0.75s;
    z-index: -2;
}

/* Gentle waves - Single calm ripple for Real Images */
.real-detected-wave::after {
    content: '';
    position: absolute;
    top: -12px;
    left: -12px;
    right: -12px;
    bottom: -12px;
    border-radius: 50%;
    border: 2px solid rgba(122, 218, 165, 0.3);
    animation: gentle-ripple 3s infinite;
    z-index: -1;
}

/* Warning Animations - Fast, aggressive, attention-grabbing */
@keyframes warning-pulse {
    0%, 100% { 

        transform: scale(1);
        filter: brightness(1);
    }
    25% { 

        transform: scale(1.03);
        filter: brightness(1.2);
    }
    75% { 

        transform: scale(1.01);
        filter: brightness(1.1);
    }
}

@keyframes warning-shake {
    0%, 100% { transform: translateX(0) rotate(0deg); }
    10% { transform: translateX(-1px) rotate(-0.5deg); }
    20% { transform: translateX(1px) rotate(0.5deg); }
    30% { transform: translateX(-1px) rotate(-0.5deg); }
    40% { transform: translateX(1px) rotate(0.5deg); }
    50% { transform: translateX(0) rotate(0deg); }
}

@keyframes warning-ripple-1 {
    0% {
        transform: scale(1);
        opacity: 0.8;
        border-width: 3px;
    }
    50% {
        opacity: 0.4;
        border-width: 2px;
    }
    100% {
        transform: scale(1.4);
        opacity: 0;
        border-width: 1px;
    }
}

@keyframes warning-ripple-2 {
    0% {
        transform: scale(1);
        opacity: 0.6;
        border-width: 2px;
    }
    100% {
        transform: scale(1.6);
        opacity: 0;
        border-width: 1px;
    }
}

/* Success Animations - Slow, calm, reassuring */
@keyframes success-pulse {
    0%, 100% { 

        transform: scale(1);
    }
    50% { 

        transform: scale(1.01);
    }
}

@keyframes gentle-float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-3px); }
}

@keyframes gentle-ripple {
    0% {
        transform: scale(1);
        opacity: 0.6;
    }
    100% {
        transform: scale(1.3);
        opacity: 0;
    }
}

/* Enhanced Modal System - Responsive */
@keyframes fade-in {
    0% { opacity: 0; }
    100% { opacity: 1; }
}

@keyframes scale-in {
    0% { 
        transform: scale(0.9) translateY(20px);
        opacity: 0;
    }
    100% { 
        transform: scale(1) translateY(0);
        opacity: 1;
    }
}

@keyframes spin-slow {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Responsive modal adjustments */
@media (max-width: 480px) {
    .fixed.inset-0 .relative {
        margin: 1rem;
        max-width: calc(100vw - 2rem);
        max-height: calc(100vh - 2rem);
        overflow-y: auto;
    }

    .relative.bg-white\/95 {
        padding: 1.5rem;
        border-radius: 1rem;
    }

    /* Adjust modal text sizes */
    .text-5xl {
        font-size: 2rem;
    }
}

@media (min-width: 481px) and (max-width: 768px) {
    .fixed.inset-0 .relative {
        margin: 1.5rem;
        max-width: calc(100vw - 3rem);
    }

    .relative.bg-white\/95 {
        padding: 2rem;
    }
}

/* Modal backdrop improvements */
.modal-backdrop {
    backdrop-filter: blur(12px);
    background: rgba(0, 0, 0, 0.6);
    transition: all 0.3s ease;
}

@media (max-width: 768px) {
    .modal-backdrop {
        backdrop-filter: blur(8px);
    }
}

.animate-fade-in {
    animation: fade-in 0.5s ease-out;
}

.animate-scale-in {
    animation: scale-in 0.6s cubic-bezier(0.34, 1.56, 0.64, 1);
}

.animate-spin-slow {
    animation: spin-slow 3s linear infinite;
}

/* Additional Professional Modal Animations */
@keyframes modal-entrance {
    0% { 
        transform: scale(0.8) translateY(40px) rotateX(15deg);
        opacity: 0;
        filter: blur(10px);
    }
    50% {
        transform: scale(1.05) translateY(-10px) rotateX(5deg);
        opacity: 0.8;
        filter: blur(2px);
    }
    100% { 
        transform: scale(1) translateY(0) rotateX(0deg);
        opacity: 1;
        filter: blur(0px);
    }
}

@keyframes reverse-spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(-360deg); }
}

@keyframes pulse-gentle {
    0%, 100% { 
        transform: scale(1);
        opacity: 1;
    }
    50% { 
        transform: scale(1.1);
        opacity: 0.8;
    }
}

@keyframes ping-slow {
    0% {
        transform: scale(1);
        opacity: 0.8;
    }
    75%, 100% {
        transform: scale(1.5);
        opacity: 0;
    }
}

@keyframes ping-slower {
    0% {
        transform: scale(1);
        opacity: 0.6;
    }
    75%, 100% {
        transform: scale(2);
        opacity: 0;
    }
}

@keyframes text-glow {
    0%, 100% { color: #3b82f6; }
    50% { color: #8b5cf6; }
}

@keyframes number-pulse {
    0%, 100% { 
        transform: scale(1);
        filter: brightness(1);
    }
    50% { 
        transform: scale(1.1);
        filter: brightness(1.2);
    }
}

@keyframes progress-fill {
    0% { width: 0%; }
    100% { width: 75%; }
}

@keyframes shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

@keyframes float-delayed {
    0%, 100% { transform: translateY(0px) translateX(20px) rotate(0deg); }
    50% { transform: translateY(-20px) translateX(20px) rotate(5deg); }
}

.animate-modal-entrance {
    animation: modal-entrance 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.animate-reverse-spin {
    animation: reverse-spin 4s linear infinite;
}

.animate-pulse-gentle {
    animation: pulse-gentle 2s ease-in-out infinite;
}

.animate-ping-slow {
    animation: ping-slow 3s cubic-bezier(0, 0, 0.2, 1) infinite;
}

.animate-ping-slower {
    animation: ping-slower 4s cubic-bezier(0, 0, 0.2, 1) infinite;
}

.animate-text-glow {
    animation: text-glow 2s ease-in-out infinite;
}

.animate-number-pulse {
    animation: number-pulse 1.5s ease-in-out infinite;
}

.animate-progress-fill {
    animation: progress-fill 5s ease-out;
}

.animate-shimmer {
    animation: shimmer 2s ease-in-out infinite;
}

.animate-float-delayed {
    animation: float-delayed 6s ease-in-out infinite;
}

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import Home from "@/pages/home";
import HowItWorks from "@/pages/how-it-works";
import About from "@/pages/about";
import FAQ from "@/pages/faq";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/how-it-works" component={HowItWorks} />
      <Route path="/about" component={About} />
      <Route path="/faq" component={FAQ} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

