      {/* Download Report Ad Modal */}
      {showDownloadAdModal && (
        <AdModal 
          isOpen={showDownloadAdModal}
          onClose={() => setShowDownloadAdModal(false)}
          onContinue={() => {
            setShowDownloadAdModal(false);
            generatePDFReport();
          }}
        />
